public class Q4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char alpha = 'A';

	    if (alpha >= 'a' && alpha <= 'z') 
		{
	        System.out.printf("Lowercase");
	    }
	    else if (alpha >= 'A' && alpha <= 'Z') 
		{
	        System.out.printf("Uppercase");
	    }
	    else 
		{
	        System.out.printf("Not an alphabet");
	    }
	}

}
