package Assignment4;

public class Q1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int table = 1;
		int num=17;

		
		System.out.printf("Table of %d is\n", num);
		
		while(table <= 10)
		{
			System.out.printf("%d\n", table * num);
			table++;
		}
	}

}
